import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import { MDBIcon,MDBCol,MDBInput,MDBCard, MDBCardBody} from "mdbreact";

class Dashboard extends Component {

  constructor(props) {
    super(props);

    this.state = {
      titre: '',
      description:'',
      date:'',
      horaire: '',
      duree: '',
      nbr_place_dispo:'',
      nbr_place_reserve:'',
      photo_produit:'',
      prix: '',

    };

    this.onChange = this.onChange.bind(this)
    this.handleUploadImage = this.handleUploadImage.bind(this);
  }
  onChange(event) {
    this.setState({
        [event.target.name]: event.target.value
    })
}
  handleUploadImage(ev) {
    ev.preventDefault();

    const data = new FormData();
    data.append('photo_produit', this.uploadInput.files[0]);
    data.append('titre',this.state.titre);
    data.append('description',this.state.description);
    data.append('date',this.state.date);
    data.append('horaire',this.state.horaire);
    data.append('nbr_place_dispo',this.state.nbr_place_dispo);
    data.append('nbr_place_reserve',this.state.nbr_place_reserve);
    data.append('prix',this.state.prix);

    fetch('http://localhost:8080/atelier', {
      method: 'POST',
      body: data,
    }).then((response) => {
      response.json().then((body) => {
        this.setState({ photo_produit: `http://localhost:8080/atelier/${body.photo_produit}` });
        console.log('ity ilay photo :',body.photo_produit);

      });
    });
  }

  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };

  render() {
    const { user } = this.props.auth;

    return (
      <div className="container-fluid">

          <div className="landing-copy col s12 center-align">
            <h4>
              <b>Administration</b> {user.name.split(" ")[0]}
            </h4>
            <button
              style={{
                width: "auto",
                borderRadius: "3px",
                letterSpacing: "1.5px",
                marginTop: "1rem"
              }}
              onClick={this.onLogoutClick}
              className="btn btn-primary"
            >
              Deconnexion
            </button>

            <center>
      <MDBCol md="6">
            <MDBCard width="50%">
              <MDBCardBody>
                <form  onSubmit={this.handleUploadImage}>
                  <p className="h4 text-center py-4">Ajouter nouveau atelier</p>
                  <div className="grey-text">
                    <MDBInput
                      label="Nom du produit"
                      group
                      type="text"
                      validate
                      error="wrong"
                      success="right" value={this.state.value}  onChange={this.onChange} name="titre"
                    />
                    <MDBInput
                      label="DÃ©scription"
                      group
                      type="text"
                      validate
                      error="wrong"
                      success="right" value={this.state.value} onChange={this.onChange} name="description"
                    />
                     <MDBInput
                      label="date"
                      group
                      type="date"
                      validate
                      error="wrong"
                      success="right" value={this.state.value} onChange={this.onChange} name="date"
                    />
                    <MDBInput
                      label="Horaire de debut"
                      group
                      type="text"
                      validate
                      error="wrong"
                      success="right" value={this.state.value} onChange={this.onChange} name="horaire"
                    />
                    <MDBInput
                      label="nombre de place disponible"
                      group
                      type="number"
                      validate
                      error="wrong"
                      success="right" value={this.state.value} onChange={this.onChange} name="nbr_place_dispo"
                    />
                    <MDBInput
                      label="nombre de place reserve"
                      group
                      type="number"
                      validate
                      error="wrong"
                      success="right" value={this.state.value} onChange={this.onChange} name="nbr_place_reserve"
                    />

                    <MDBInput
                      label="Prix"
                      group
                      type="text"
                      validate
                      error="wrong"
                      success="right" value={this.state.value} onChange={this.onChange}  name="prix"
                    />
                  <label>Images de l'atelier : </label>  
                  <input ref={(ref) => { this.uploadInput = ref; }} type="file" name="photo_produit"/>
                  </div>
                  <div className="text-center">
                  <div className="text-center mt-4">
                <button className="btn btn-outline-warning" type="submit">
                  Ajouter
                  <MDBIcon icon="paper-plane" className="ml-2" />
                </button>
              </div>
              </div>
                </form>
              </MDBCardBody>
            </MDBCard>
          </MDBCol>
          </center>
          </div>
        </div>
    );
  }
}

Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard);
