import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { logoutUser } from "../../actions/authActions";
import { Router , Link, Route } from 'react-router-dom';
import Atelier from './../atelier/atelier'
import Liste from './../atelier/getatelier'
import { MDBNavbar, MDBNavbarBrand, MDBNavbarNav, MDBNavItem, MDBNavLink, MDBNavbarToggler, MDBCollapse } from "mdbreact";


import '@trendmicro/react-sidenav/dist/react-sidenav.css';

class Dashboard extends Component {


  state = {
    modal5: false,

  }


  //popops login fonction 
  toggleCollapse = collapseID => () =>
    this.setState(prevState => ({
      collapseID: prevState.collapseID !== collapseID ? collapseID : ""
    }));
  toggle = nr => () => {
    let modalNumber = "modal" + nr;
    this.setState({
      [modalNumber]: !this.state[modalNumber]
    });
  }
  onLogoutClick = e => {
    e.preventDefault();
    this.props.logoutUser();
  };


  render() {

    const { user } = this.props.auth;

    return (
      <div className="container-fluid">

          <MDBNavbar color="red" dark expand="md" style={{ marginTop: "1px" }} id="navbar">
          <MDBNavbarBrand>
            <img src="logo.png" alt="Logo" id="logoimage"/>
          </MDBNavbarBrand>
          <MDBNavbarToggler onClick={this.toggleCollapse("navbarCollapse3")} />
          <MDBCollapse id="navbarCollapse3" isOpen={this.state.collapseID} navbar>
            <MDBNavbarNav left>

            </MDBNavbarNav>
            <MDBNavbarNav right>
              <MDBNavItem>
                <MDBNavLink to="" onClick={this.onLogoutClick} className="nav-header" >Deconnexion</MDBNavLink>
              </MDBNavItem>
            </MDBNavbarNav>
          </MDBCollapse>
        </MDBNavbar>


            <div className="wrapper">
              <nav id="sidebar">
              <center>
                  <div className="sidebar-header">
                  <Router>
                    <Route exact path='/dashboard/ajout' component={Atelier} />
                    <Route exact path='/dashboard/liste' component={Liste} />
                    <img src="logo.png" alt="logo" id="imagedash"/>
                        <h3 id="h3header">{user.name.split(" ")[0]}</h3>
                        <a id="li1" href="#"><Link  to="/dashboard/ajout">Ajouter nouveau atelier</Link></a><br/>
                        <a id="li1" href="#"><Link to="/dashboard/liste">Voir tous les ateliers</Link></a>
                  </Router>
                  </div>
                  </center>
              </nav>

            </div>

            <div>    
</div>

      </div>
    );
  }
}

Dashboard.propTypes = {
  logoutUser: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  auth: state.auth
});

export default connect(
  mapStateToProps,
  { logoutUser }
)(Dashboard);
